import Field from "../common/Field/Field";

const MovieDetails = (props) => {
  const { movie } = props;
  const options = {
    year: "numeric",
    month: "long",
    day: "numeric",
  };

  const title = `${movie.date.toLocaleDateString(
    "fr-FR",
    options
  )} en salle / ${movie.duration} / ${movie.gender}`;

  return (
    <div>
      <Field value={title} />
      <Field header="Par" value={movie.by} />
      <Field header="Avec" value={movie.actors} />
    </div>
  );
};

export default MovieDetails;
