import Field from "../common/Field/Field";

const MovieDetails = (props) => {
  const { movie } = props;
  const options = {
    weekday: "long",
    year: "numeric",
    month: "long",
    day: "numeric",
  };

  const title = `${movie.date.toLocaleDateString(
    "fr-FR",
    options
  )} en salle / ${movie.duration} / ${movie.genre}`;

  return (
    <div>
      <Field value={title} />
    </div>
  );
};

export default MovieDetails;
