import BasketDetail from "../../components/BasketDetail/BasketDetail";
import { connect } from "react-redux";

import "./Basket.scss";

const Basket = (props) => {
  return (
    <div className="basket">
      Panier de location
      <BasketDetail />
    </div>
  );
};

const basketToProps = (state) => {
  return {
    basketItems: state.movieStore.basket,
    basketItemCount: state.movieStore.basket.reduce((count, curItem) => {
      return count + curItem.quantity;
    }, 0),
    totalPrice: state.movieStore.basket.reduce((count, curItem) => {
      return count + curItem.price * curItem.quantity;
    }, 0),
  };
};

export default connect(basketToProps, null)(Basket);
