import "./BasketDetail.scss";

const BasketDetail = (props) => {
  const { title, price, imageUrl } = props.item;
  const imageRef = React.createRef();

  return (
    <div className="basketDetail">
      <img className="basket__img" src={imageUrl} alt={title} ref={imageRef} />
      <span className="basketDetail__title">{title}</span>
      <span className="basketDetail__price">â‚¬{price}</span>
    </div>
  );
};

export default BasketDetail;
