import { connect } from "react-redux";
import { FilterType } from "../../../data/filter/filterType";
import { movies } from "../../../data/movies";
import FilterCheckBox from "./FilterCheckBox/FilterCheckBox";
import "./FilterPanel.scss";
import FilterRadio from "./FilterRadio/FilterRadio";

const FilterPanel = (props) => {
  const { values, title, filterType, dispatch, genderCount } = props;

  const handleSelectBox = (name) => {
    dispatch(addGenderToFilter(name));
  };

  const handleUnSelectBox = (name) => {
    dispatch(removeGenderFromFilter(name));
  };

  return (
    <div className="filterPanel">
      <p className="title">{title}</p>
      {values.sort().map((value, index) => {
        const count = filterType === FilterType.CheckBox && genderCount[value];
        return (
          <div key={index}>
            {filterType === FilterType.CheckBox && (
              <FilterCheckBox
                label={value}
                count={count}
                onSelect={handleSelectBox}
                onUnSelect={handleUnSelectBox}
              />
            )}
            {filterType === FilterType.Radio && <FilterRadio value={value} />}
          </div>
        );
      })}
    </div>
  );
};

const filterPanelToProps = (state) => {
  const genderCount = {};

  state.movieStore.movies.forEach((m) => {
    genderCount[m.gender] = genderCount[m.gender] + 1 || 1;
  });

  return {
    genderCount,
  };
};

export default connect(filterPanelToProps)(FilterPanel);
