import { useState } from "react";
import "./FilterRadio.scss";

const FilterRadio = (props) => {
  const { value, label, handleChange } = props;

  const [checked, setChecked] = useState(false);

  const onChange = (e) => {
    setChecked(!checked);
    handleChange(e);
  };

  return (
    <div className="radio">
      <input
        checked={checked}
        type="radio"
        value={value}
        onChange={onChange}
      ></input>
      {label}
    </div>
  );
};

export default FilterRadio;
