export const ADD_TO_BASKET = "ADD_TO_BASKET";
export const REMOVE_TO_BASKET = "REMOVE_TO_BASKET";
export const UP_QUANTITY_TO_BASKET = "UP_QUANTITY_TO_BASKET";
export const DOWN_QUANTITY_TO_BASKET = "DOWN_QUANTITY_TO_BASKET";

export const addToBasket = (movie) => {
  return {
    type: ADD_TO_BASKET,
    payload: movie,
  };
};

export const removeToBasket = (movieId) => {
  return {
    type: REMOVE_TO_BASKET,
    payload: movieId,
  };
};

export const upMovie = (movieId) => {
  return {
    type: UP_QUANTITY_TO_BASKET,
    payload: movieId,
  };
};

export const downMovie = (movieId) => {
  return {
    type: DOWN_QUANTITY_TO_BASKET,
    payload: movieId,
  };
};
