import { connect } from "react-redux";
import { filterByGender } from "../../Helpers/Filter.helper";
import MovieItem from "../MovieItem/MovieItem";
import "./MovieList.scss";

const MovieList = (props) => {
  const { movies } = props;

  return (
    <main className="grid-layout">
      <div className="dashboard">
        {movies.map((movie) => {
          // let classes = `${this.state.colValue} col-md-6 mb-4`;
          return (
            <div key={movie.id}>
              <MovieItem movie={movie} />
            </div>
          );
        })}
      </div>
    </main>
  );
};

const listToProps = (state) => {
  const genders = state.filter;
  //const orderBy = state.orderBy;

  const byGender = filterByGender(state.movieStore.movies, genders);
  //const filterByOrderArr = orderByFilter(filterByBrandArr, orderBy);

  return { movies: byGender };
};

export default connect(listToProps, null)(MovieList);
