import { FilterType } from "../../../data/filter/filterType";
import { movies } from "../../../data/movies";
import FilterCheckBox from "./FilterCheckBox/FilterCheckBox";
import "./FilterPanel.scss";
import FilterRadio from "./FilterRadio/FilterRadio";

const FilterPanel = (props) => {
  const { values, title, filterType } = props;

  return (
    <div className="filterPanel">
      <p className="title">{title}</p>
      {values.sort().map((value, index) => {
        const count =
          filterType === FilterType.CheckBox &&
          movies.filter((movie) => movie.gender === value).length;
        return (
          <div key={index}>
            {filterType === FilterType.CheckBox && (
              <FilterCheckBox label={value} count={count} />
            )}
            {filterType === FilterType.Radio && <FilterRadio label={value} />}
          </div>
        );
      })}
    </div>
  );
};

export default FilterPanel;
