import React from "react";

import RemoveIcon from "../../images/remove.png";

import "./BasketDetail.scss";

const BasketDetail = (props) => {
  const { title, price, imageUrl } = props.item;
  const imageRef = React.createRef();

  return (
    <div className="basketDetail">
      <img
        className="basketDetail__img"
        src={imageUrl}
        alt={title}
        ref={imageRef}
      />
      {/* <div className="description"> */}
      <div className="basketDetail__title">{title}</div>
      <div className="basketDetail__price">â‚¬{price}</div>
      {/* </div> */}
      <img
        className="basketDetail__img"
        src={RemoveIcon}
        alt="Remove"
        ref={imageRef}
      />
    </div>
  );
};

export default BasketDetail;
