export const ADD_TO_BASKET = "ADD_TO_BASKET";
export const REMOVE_TO_BASKET = "REMOVE_TO_BASKET";

export const addToBasket = (movie) => {
  return {
    type: ADD_TO_BASKET,
    payload: movie,
  };
};

export const removeToBasket = (movieId) => {
  return {
    type: REMOVE_TO_BASKET,
    payload: movieId,
  };
};
