import React from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { addToBasket } from "../../actions";
import Field from "../common/Field/Field";

import "./MovieItem.scss";

const MovieItem = (props) => {
  const { movie, dispatch } = props;
  const imageRef = React.createRef();
  const euroPrice = `€${movie.price}`;
  const options = {
    year: "numeric",
    month: "long",
    day: "numeric",
  };

  const releaseDate = movie.date.toLocaleDateString("fr-FR", options);

  return (
    <div className="movie">
      <img
        className="movie__img"
        src={movie.imageUrl}
        alt={movie.title}
        ref={imageRef}
      />

      <div>
        <Link to={`/movies/${movie.id}`} className="movie__link">
          {movie.title}
        </Link>

        <Field header="Genre" value={movie.gender} />
        <Field header="Date de sortie" value={releaseDate} />
        <Field value={euroPrice} />
      </div>
      <div className="movie__addToBasket">
        <button
          onClick={() => {
            dispatch(addToBasket(movie));
          }}
        >
          Add to basket
        </button>
      </div>
    </div>
  );
};

export default connect()(MovieItem);
